import React, { useState } from 'react'
import './App.css';
import PostItem from './components/ui/postitem/Postitem';
import Counter from './components/ui/counter/Counter';
import Priority from './components/Priority';
import Header from './components/header/Header';
import { Routes, Route } from 'react-router-dom';
import Editing from './components/pages/editing/Editing';
import View from './components/pages/View';

let data = [
  {
    id:1,
    date: "01.01.2023",
    priority: 1,
    marks: 3
  },
  {
    id:2,
    date: "01.01.2023",
    priority: 1,
    marks: 3
  },
  {
    id:3,
    date: "01.01.2023",
    priority: 1,
    marks: 3
  },
  {
    id:4,
    date: "01.01.2023",
    priority: 1,
    marks: 3
  }
]


function App() {


  const [selectedSort, setSelectedSort] = useState()

  const sortPosts = (sort) => {
    setSelectedSort(sort);
    setPosts([...data].sort((a,b) => a[sort].localeCompare(b[sort])))
  }

  return (
    <div className="App">
      <Routes>
        <Route path={`/editing`} element={<Editing/>}/>
        <Route path={`/home`} element={<Home/>}/>
        <Route path={`/view`} element={<View/>}/>
      </Routes>

      <Header />


      <div className='App_content'>
        <div className="aside">
          <Priority />
        </div>
        <Priority
           value={selectedSort}
           onChange={sortPosts}
           defaultValue="Сортировка"
           option={[
            {value: "date", name: "Новые"}
           ]}
       />

        <div className="main">
          <Counter name="Добавить задачу" adress="/editing"/>
          {data.map(item => <PostItem key={item.id} id={item.id} date={item.date} priority={item.priority} marks={item.marks} />)}
        </div>
      </div>
    </div>


  );
}

export default App;
